package exemplos;

public class ExemploDeclaraInicializaIntDinamico {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] numeros = new int[4];

		// Inicializando um array de inteiros
		numeros[0] = 10;
		numeros[1] = 5;
		numeros[2] = 6;
		numeros[3] = 7;
		
		
		// Declara
		String[] nomes = new String[4];

		// Inicialização
		nomes[0] = "Carlos";
		nomes[1] = "Juliana";
		nomes[2] = "Pedro";
		nomes[3] = "Claudia";
		
	}

}
