package exemplos;

public class ExemploDeclaraInicilizaInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Declarando e inicializando um array de inteiros – Inicialização estática
		int[] numeros = { 1, 2, 3, 4, 5 };

		// Acessando elementos do array
		System.out.println( "O primeiro elemento é " + numeros[0] );
		System.out.println( "O primeiro elemento é " + numeros[1] );
		System.out.println( "O primeiro elemento é " + numeros[2] );
		System.out.println( "O primeiro elemento é " + numeros[3] );
		System.out.println( "O primeiro elemento é " + numeros[4] );	
	}

}
